import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Card, AppProvider } from "@shopify/polaris";
import Skeleton from "./Skeleton";
import nasa from "./nasa.jpg";

const Item = styled.div`
  margin-left: 1vw;
  height: 460px;
  width: 580px;
  border: solid #9daaf2 3px;
  border-radius: 0.7rem;
  margin-top: 1.8rem;
  background: #1a2238;
  position: relative;

  @media screen and (max-width: 760px) {
    height: 480px;
    width: 380px;
  }
`;

const Header = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const CardLogo = styled.img`
  width: 45px;
  height: 45px;
  margin-left: 30px;
  margin-top: 10px;
  border-radius: 50%;
  border: solid #fff 0.2px;
`;

const Cardh1 = styled.h1`
  margin-left: 10px;
  margin-top: 10px;
  font-size: 30px;
  color: #f7d679;
`;

const CardImage = styled.img`
  width: 510px;
  height: 250px;
  margin-left: 30px;
  margin-top: 15px;
  border: solid #fff 0.2px;
  border-radius: 0.4rem;

  @media screen and (max-width: 760px) {
    width: 320px;
    height: 260px;
  }
`;

const CardDescription = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  width: 250px;
  margin-bottom: -20px;
  margin-left: 30px;
  color: #9daaf2;
`;

const CardShareInfo = styled.div`
  display: flex;
  align-items: center;
  margin-top: 10px;
`;

const LikeButton = styled.div``;

const ShareButton = styled.img``;

const Date = styled.div``;

const Info = styled.div``;

const Title = styled.div``;

const Description = styled.div``;

function Verticalscroll() {
  const [loading, setloading] = useState(false);

  useEffect(() => {
    setloading(true);
    setTimeout(() => {
      setloading(false);
    }, 3000);
  }, []);

  return (
    <>
      {loading ? (
        <>
          <Skeleton />
        </>
      ) : (
        <>
          <AppProvider>
            <Card style={{ positon: "absolute" }}>
              <Item>
                <Header>
                  <CardLogo src={nasa} />
                  <Cardh1>NASA</Cardh1>
                </Header>
                <CardImage src={""} alt="" />
                <CardDescription>
                  <CardShareInfo>
                    <LikeButton>
                      <svg
                        aria-label="Unlike"
                        class="_8-yf5 "
                        fill="#ed4956"
                        height="24"
                        role="img"
                        viewBox="0 0 48 48"
                        width="24"
                      >
                        <path d="M34.6 3.1c-4.5 0-7.9 1.8-10.6 5.6-2.7-3.7-6.1-5.5-10.6-5.5C6 3.1 0 9.6 0 17.6c0 7.3 5.4 12 10.6 16.5.6.5 1.3 1.1 1.9 1.7l2.3 2c4.4 3.9 6.6 5.9 7.6 6.5.5.3 1.1.5 1.6.5s1.1-.2 1.6-.5c1-.6 2.8-2.2 7.8-6.8l2-1.8c.7-.6 1.3-1.2 2-1.7C42.7 29.6 48 25 48 17.6c0-8-6-14.5-13.4-14.5z"></path>
                      </svg>
                    </LikeButton>
                    <ShareButton src={""} alt="" />
                    <Date>11/09/2021</Date>
                  </CardShareInfo>
                  <Info>
                    <Title></Title>
                    <Description></Description>
                  </Info>
                </CardDescription>
              </Item>
              <Item>
                <CardImage src={""} alt="" />
                <CardDescription>
                  <h1
                    style={{
                      fontSize: "20px",
                      marginBottom: "-20px",
                      padding: "0",
                      fontWeight: "bold",
                    }}
                  >
                    Crypto Market Tracker
                  </h1>
                  <br /> Libraries Used:
                  <br />- Axios [For API]
                  <br />- Framer Motion[Animation]
                  <br />- Styled-Components[CSS]
                </CardDescription>
              </Item>
            </Card>
          </AppProvider>
        </>
      )}
    </>
  );
}

export default Verticalscroll;
