import React from "react";
import Scroll from "react-scroll";
import styled from "styled-components";
import logo from "./logo.png";
import Carousel from "react-elastic-carousel";

var Element = Scroll.Element;

const Post = styled.div``;

const CardDescription = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  width: 250px;
  margin-bottom: -20px;
  margin-left: 30px;
  color: #fff;
`;

const Item = styled.div`
  margin-left: 1vw;
  height: 480px;
  width: 320px;
  border: solid #d72323 5px;
  border-radius: 1.2rem;
  display: flex;
  flex-direction: column;
`;

const CardImage = styled.img`
  width: 240px;
  height: 170px;
  margin-left: 35px;
  margin-top: 35px;
  border: solid #fff 0.2px;
  border-radius: 1rem;
`;

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 1 },
  { width: 768, itemsToShow: 2.5 },
  { width: 1200, itemsToShow: 3.5 },
];

function Verticalscroll() {
  return (
    <CoinContainer>
      <CoinRow>
        <ImageContainer src={image} />
        <CoinNameHolder>{name}</CoinNameHolder>
        <CoinSymbol>{symbol.toUpperCase()}</CoinSymbol>
        <CoinPrice>${price}</CoinPrice>
        {percentage < 0 ? (
          <CoinPercentageChangeRed>
            {percentage.toFixed(2)}%
          </CoinPercentageChangeRed>
        ) : (
          <CoinPercentageChangeGreen>
            {percentage.toFixed(2)}%
          </CoinPercentageChangeGreen>
        )}
        <CoinMrtCap>Mkt Cap:${mktcap}</CoinMrtCap>
      </CoinRow>
    </CoinContainer>
  );
}

export default Verticalscroll;
