import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Card, AppProvider } from "@shopify/polaris";
import Skeleton from "./Skeleton";
import nasa from "./nasa.png";

const Item = styled.div`
  margin-left: 1vw;
  height: 460px;
  width: 580px;
  border: solid #9daaf2 3px;
  border-radius: 0.7rem;
  margin-top: 1.8rem;
  background: #1a2238;
  position: relative;

  @media screen and (max-width: 760px) {
    height: 480px;
    width: 380px;
  }
`;

const Header = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const CardLogo = styled.img`
  width: 45px;
  height: 45px;
  margin-left: 30px;
  margin-top: 10px;
  border-radius: 50%;
  border: solid #fff 0.2px;
`;

const Cardh1 = styled.h1`
  margin-left: 10px;
  margin-top: 10px;
  font-size: 30px;
  color: #f7d679;
`;

const CardImage = styled.img`
  width: 510px;
  height: 250px;
  margin-left: 30px;
  margin-top: 10px;
  border: solid #fff 0.2px;
  border-radius: 0.4rem;

  @media screen and (max-width: 760px) {
    width: 320px;
    height: 260px;
  }
`;

const CardDescription = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  width: 250px;
  margin-bottom: -20px;
  margin-left: 30px;
  color: #9daaf2;
`;

const CardShareInfo = styled.div``;

const LikeButton = styled.img``;

const ShareButton = styled.img``;

const Date = styled.div``;

const Info = styled.div``;

const Title = styled.div``;

const Description = styled.div``;

function Verticalscroll() {
  const [loading, setloading] = useState(false);

  useEffect(() => {
    setloading(true);
    setTimeout(() => {
      setloading(false);
    }, 5000);
  }, []);

  return (
    <>
      {loading ? (
        <>
          <Skeleton />
        </>
      ) : (
        <>
          <AppProvider>
            <Card style={{ positon: "absolute" }}>
              <Item>
                <Header>
                  <CardLogo src={nasa} />
                  <Cardh1>NASA</Cardh1>
                </Header>
                <CardImage src={""} alt="" />
                <CardDescription>
                  <CardShareInfo>
                    <LikeButton src={""} alt="" />
                    <ShareButton src={""} alt="" />
                    <Date></Date>
                  </CardShareInfo>
                  <Info>
                    <Title></Title>
                    <Description></Description>
                  </Info>
                </CardDescription>
              </Item>
              <Item>
                <CardImage src={""} alt="" />
                <CardDescription>
                  <h1
                    style={{
                      fontSize: "20px",
                      marginBottom: "-20px",
                      padding: "0",
                      fontWeight: "bold",
                    }}
                  >
                    Crypto Market Tracker
                  </h1>
                  <br /> Libraries Used:
                  <br />- Axios [For API]
                  <br />- Framer Motion[Animation]
                  <br />- Styled-Components[CSS]
                </CardDescription>
              </Item>
            </Card>
          </AppProvider>
        </>
      )}
    </>
  );
}

export default Verticalscroll;
